import React, { useState } from 'react';
import './App.css';
import CourseList from './components/List';
import Search from './components/Search';
import Total from './components/Total';
import CourseForm from './components/CourseForm'; // Import the new component

function App() {
  // ... (other code remains the same)

  return (
    <div className="App">
      <div className="form-container">
        <CourseForm
          addCourse={addCourse}
          courseName={courseName}
          setCourseName={setCourseName}
          courseNumber={courseNumber}
          setCourseNumber={setCourseNumber}
          units={units}
          setUnits={setUnits}
          grade={grade}
          setGrade={setGrade}
        />
        <div className="course-container">
          <div className='right-column'>
            <h2 id='title'>Course & Grade List</h2>
            <Search searchTerm={searchTerm} handleSearchChange={handleSearchChange} />
            <CourseList courses={courses} searchTerm={searchTerm} handleSearchChange={handleSearchChange}/>
            <Total calculateQPI={calculateQPI} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
