import React, { useState } from 'react';
import './App.css';
import CourseList from './components/List';

function App() {
  const [courses, setCourses] = useState([
    {
      courseName: 'Course 1',
      courseNumber: '101',
      units: 3,
      grade: 'A',
    },
    {
      courseName: 'Course 2',
      courseNumber: '101',
      units: 4,
      grade: 'B+',
    },
  ]);
  const [courseNumber, setCourseNumber] = useState('');
  const [courseName, setCourseName] = useState('');
  const [units, setUnits] = useState('');
  const [grade, setGrade] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const gradeMapping = {
    A: 4,
    'B+': 3.5,
    B: 3,
    'C+': 2.5,
    C: 2,
    D: 1,
    F: 0,
  };

  const addCourse = () => {
    if (courseName && courseNumber && units && grade) {
      const course = {
        courseName,
        courseNumber,
        units: parseFloat(units),
        grade,
      };
      setCourses([...courses, course]);
      setCourseName('');
      setCourseNumber('');
      setUnits('');
      setGrade('');
    }
  };

  const calculateQPI = () => {
    const totalUnits = courses.reduce((total, course) => total + course.units, 0);
    const totalGradePoints = courses.reduce((total, course) => total + gradeMapping[course.grade] * course.units, 0);
    return totalGradePoints / totalUnits;
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="App">
      <div className="form-container">
        <div className="left-column">
        <h2>Add Course</h2>
        <div className="label">
          <label>Course Number</label>
          <input type="text" placeholder="e.g. 4-125" value={courseNumber} onChange={(e) => setCourseNumber(e.target.value)} />
        </div>
        <div className="label">
          <label>Course Name</label>
          <input type="text" placeholder="e.g. IT Elective 2" value={courseName} onChange={(e) => setCourseName(e.target.value)} />
        </div>
        <div className="label">
          <label>Units</label>
          <input type="number" placeholder="#" value={units} onChange={(e) => setUnits(e.target.value)} />
        </div>
          <div className="radio-group">
            <label className='label'> Grades </label>
            <label class="custom-radio-button">
              <input type="radio" name="grade" value="A" checked={grade === 'A'} onChange={() => setGrade('A')} /> 
              <span class="helping-el"></span>
              <span class="label-text">A</span> 
            </label>
            <label class="custom-radio-button">
              <input type="radio" name="grade" value="B+" checked={grade === 'B+'} onChange={() => setGrade('B+')} /> 
              <span class="helping-el"></span>
              <span class="label-text">B+</span> 
            </label>
            <label class="custom-radio-button">
              <input type="radio" name="grade" value="B" checked={grade === 'B'} onChange={() => setGrade('B')} /> 
              <span class="helping-el"></span>
              <span class="label-text">B</span> 
            </label>
            <label class="custom-radio-button">
              <input type="radio" name="grade" value="C+" checked={grade === 'C+'} onChange={() => setGrade('C+')} /> 
              <span class="helping-el"></span>
              <span class="label-text">C+</span> 
            </label>
            <label class="custom-radio-button">
              <input type="radio" name="grade" value="C" checked={grade === 'C'} onChange={() => setGrade('C')} /> 
              <span class="helping-el"></span>
              <span class="label-text">C</span> 
            </label>
            <label class="custom-radio-button">
              <input type="radio" name="grade" value="D" checked={grade === 'D'} onChange={() => setGrade('D')} /> 
              <span class="helping-el"></span>
              <span class="label-text">D</span> 
            </label>
            <label class="custom-radio-button">
              <input type="radio" name="grade" value="F" checked={grade === 'F'} onChange={() => setGrade('F')} /> 
              <span class="helping-el"></span>
              <span class="label-text">F</span> 
            </label>
          </div>
          <button onClick={addCourse}>Submit</button>
        </div>
        <div className="course-container">
          <div className='right-column'>
            <h2 id='title'>Course & Grade List</h2>
            <CourseList courses={courses} searchTerm={searchTerm} handleSearchChange={handleSearchChange}/>
            <div className="qpi">
              <div className="qpi-label">
                <p>Total QPI:</p>
              </div>
              <div className="qpi-value">
                <p>{calculateQPI().toFixed(2)}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
