import React, { useState } from 'react';
import './App.css';
import CourseList from './CourseList'; // Import the CourseList component

function App() {
  const [courses, setCourses] = useState([]);
  const [courseName, setCourseName] = useState('');
  const [courseNumber, setCourseNumber] = useState('');
  const [units, setUnits] = useState('');
  const [grade, setGrade] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const gradeMapping = {
    A: 4,
    'B+': 3.5,
    B: 3,
    'C+': 2.5,
    C: 2,
    D: 1,
    F: 0,
  };

  const addCourse = () => {
    if (courseName && courseNumber && units && grade) {
      const course = {
        courseName,
        courseNumber,
        units: parseFloat(units),
        grade,
      };
      setCourses([...courses, course]);
      setCourseName('');
      setCourseNumber('');
      setUnits('');
      setGrade('');
    }
  };

  const calculateQPI = () => {
    const totalUnits = courses.reduce((total, course) => total + course.units, 0);
    const totalGradePoints = courses.reduce((total, course) => total + gradeMapping[course.grade] * course.units, 0);
    return totalGradePoints / totalUnits;
  };

  const filteredCourses = courses.filter(course =>
    course.courseName.toLowerCase().includes(searchTerm.toLowerCase()) ||
    course.courseNumber.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="App">
      <div className="container">
        <div className="left-column">
          {/* ... (existing code) */}
          <button onClick={addCourse}>Add Course</button>
        </div>
        {/* Use the CourseList component */}
        <CourseList courses={filteredCourses} searchTerm={searchTerm} calculateQPI={calculateQPI} />
      </div>
      <div className="qpi">
        <p>Total QPI: {calculateQPI().toFixed(2)}</p>
      </div>
    </div>
  );
}

export default App;
