import React, { useState } from 'react';
import './App.css';
import CourseList from './components/List';


function App() {
  const [courses, setCourses] = useState([]);
  const [courseName, setCourseName] = useState('');
  const [courseNumber, setCourseNumber] = useState('');
  const [units, setUnits] = useState('');
  const [grade, setGrade] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const gradeMapping = {
    A: 4,
    'B+': 3.5,
    B: 3,
    'C+': 2.5,
    C: 2,
    D: 1,
    F: 0,
  };

  const addCourse = () => {
    if (courseName && courseNumber && units && grade) {
      const course = {
        courseName,
        courseNumber,
        units: parseFloat(units),
        grade,
      };
      setCourses([...courses, course]);
      setCourseName('');
      setCourseNumber('');
      setUnits('');
      setGrade('');
    }
  };

  const calculateQPI = () => {
    const totalUnits = courses.reduce((total, course) => total + course.units, 0);
    const totalGradePoints = courses.reduce((total, course) => total + gradeMapping[course.grade] * course.units, 0);
    return totalGradePoints / totalUnits;
  };

  const filteredCourses = courses.filter(course =>
    course.courseName.toLowerCase().includes(searchTerm.toLowerCase()) ||
    course.courseNumber.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="App">
      <div className="container">
        <div className="left-column">
        <h2>Add Course</h2>
          <input type="text" placeholder="Course Name" value={courseName} onChange={(e) => setCourseName(e.target.value)} />
          <input type="text" placeholder="Course Number" value={courseNumber} onChange={(e) => setCourseNumber(e.target.value)} />
          <input type="number" placeholder="Units" value={units} onChange={(e) => setUnits(e.target.value)} />
          <div className="radio-group">
            <label>
              <input type="radio" name="grade" value="A" checked={grade === 'A'} onChange={() => setGrade('A')} /> A
            </label>
            <label>
              <input type="radio" name="grade" value="B+" checked={grade === 'B+'} onChange={() => setGrade('B+')} /> B+
            </label>
            {/* Add radio buttons for other grades */}
          </div>
          <button onClick={addCourse}>Add Course</button>
        </div>
        <CourseList courses={filteredCourses} searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
      </div>
      <div className="qpi">
        <p>Total QPI: {calculateQPI().toFixed(2)}</p>
      </div>
    </div>
  );
}

export default App;
